import React, { Component } from "react"
import { Line } from "react-chartjs-2"
import { bitcoinService } from "../services/bitcoin.service"
import Chart from "chart.js/auto" //Nedded for category scale

import { useForm } from "../customHooks/useForm"
import { useEffect, useState } from "react"

export function MarketPrice() {
  const [marketPrice, setMarketPrice] = useState(null)
  const [timePeriod,handleChange,setTimePeriod] = useForm()
  // const [timePeriod, setTimePeriod] = useState("10")

  useEffect(() => {
    loadMarketPrice()
  }, [])

  const loadMarketPrice = async () => {
    
    try {
      let updatedMarketPrice = await bitcoinService.getMarketPrice(timePeriod)
      
      let xVals
      xVals = updatedMarketPrice.values.map((val) => new Date(val.x * 1000).toLocaleDateString())
      let newData = {
        labels: xVals,
        datasets: [
          {
            label: `BTC Market Price - ${timePeriod} Months`,
            data: updatedMarketPrice.values.filter((val) => val.y),
            fill: true,
            backgroundColor: "rgba(75,192,192,0.2)",
            borderColor: "rgba(75,192,192,1)",
          },
        ],
      }
      setMarketPrice(newData)
    } catch (err) {
      console.log("err:", err)
    }
  }

  const onSetTimePeriod = (e) => {
    handleChange()
    console.log(e.target.value)
    setTimePeriod(e.target.value)
    loadMarketPrice()
  }

  if (!marketPrice) return <span>Loading</span>
  return (
    <div className='MarketPrice'>
           <select name='timePeriod'  onChange={onSetTimePeriod} id="timePeriod"  >
        <option value='10'>10 Months</option>
        <option value='2'>2 Months</option>
        <option value='4'>4 Months</option>
        <option value='6'>6 Months</option>
        <option value='8'>8 Months</option>
        <option value='12'>12 Months</option>
      </select>
      <Line data={marketPrice} />
    </div>
  )
}

